import { USER_LOADED, USER_LOADING } from 'Store/constants/auth.constant'

const initialState = {
  token: localStorage.getItem('jwtoken'),
  isAuth: false,
  user: null,
  isLoading: false,
}

export function AuthReducer(state = initialState, action) {
  if (action.type === USER_LOADING) return state
}
