import styled from 'styled-components'

export const LoaderDiv = styled.div`
  position: absolute;
  inset: 0;
  background-color: rgb(255 255 255 / 50%);
  z-index: 1000;
  border-radius: inherit;
  display: grid;
  place-items: center;
  .loader {
    height: 60px;
    width: 60px;
  }
`
