import styled from "styled-components";
import { color as colors } from "Assets/Style/themes";

export const MessageContainer = styled.div`
  background-color: ${({ color }) => color || colors.primary};
  font-size: 1.4rem;
  padding:.5em 1em;
  border-left: 2px solid ${({ color }) => color || 'transparent'};
  color: #fff;
  font-weight: ${({ color }) => color || 'transparent'};


  p {
    margin: 0;
  }
`;
