import { TOGGLE_DRAWER, TOGGLE_LOADER } from 'Store/constants/common.constant'

export const openDrawer = (status, id = null) => ({ type: TOGGLE_DRAWER, payload: { status, id } })
export const closeDrawer = (status, id = null) => ({ type: TOGGLE_DRAWER, payload: { status, id } })

export const toggleLoader = payload => ({ type: TOGGLE_LOADER, payload: payload })
