import { LOGIN_SUCCESS, USER_LOADED, USER_LOADING } from 'Store/constants/auth.constant'

const initialState = {
  auth: {
    token: localStorage.getItem('jwtoken'),
    user: null,
  },
  isAuth: false,
  isLoading: false,
}

export function AuthReducer(state = initialState, action) {
  if (action.type === USER_LOADING) {
    return {
      ...state,
      isLoading: true,
    }
  }
  if (action.type === USER_LOADED) {
    return {
      ...state,
      isLoading: false,
      isAuth: true,
      user: action.payload,
    }
  }
  if (action.type === LOGIN_SUCCESS) {
    localStorage.setItem('jwtoken', action.payload.token)
    return {
      ...state,
      isLoading: false,
      isAuth: true,
      user: action,
    }
  }
  if (action.type === USER_LOADING) {
    return {
      ...state,
      isLoading: true,
    }
  }
  if (action.type === USER_LOADING) {
    return {
      ...state,
      isLoading: true,
    }
  }

  return state
}
