// import { CONFIRMED_CREATE_POST_ACTION, CONFIRMED_DELETE_POST_ACTION, CONFIRMED_EDIT_POST_ACTION, CONFIRMED_GET_POST, CONFIRMED_GET_POSTS, GET_POSTS_BY_TAG, GET_SINGLE_POST, SET_SINGLE_POST } from '../constants/post.constant'

import { post_type } from 'Store/constants/post.constant'

const initialState = {
  posts: [],
  post: [],
}

export default function PostsReducer(state = initialState, { type, payload }) {
  if (actions.type === post_type.GET_POSTS) {
    return {
      ...state,
      posts: actions.payload,
    }
  }

  if (actions.type === CONFIRMED_CREATE_POST_ACTION) {
    const posts = [...state.posts, actions.payload]
    return {
      ...state,
      posts,
    }
  }

  if (actions.type === CONFIRMED_DELETE_POST_ACTION) {
    const posts = [...state.posts]
    const postIndex = posts.findIndex(post => post._id === actions.payload)
    posts.splice(postIndex, 1)
    return {
      ...state,
      posts,
    }
  }

  if (actions.type === CONFIRMED_EDIT_POST_ACTION) {
    const posts = [...state.posts]
    const postIndex = posts.findIndex(post => post._id === actions.payload._id)

    posts[postIndex] = actions.payload
    return {
      ...state,
      posts,
    }
  }

  if (actions.type === GET_POSTS_BY_TAG) {
    const posts = [...state.posts]
    const filtered_posts = posts.filter(post => post.tags.includes(actions.payload))
    return {
      ...state,
      posts: filtered_posts,
    }
  }

  if (actions.type === GET_SINGLE_POST) {
    const posts = [...state.posts]
    const post = posts.find(post => post._id === actions.payload)
    return {
      ...state,
      post,
    }
  }

  if (actions.type === SET_SINGLE_POST) {
    return {
      ...state,
      post: actions.payload,
    }
  }

  return state
}
