import { INCREMENT, DECREMENT, RESET } from 'Store/constants/Counter.constant';

export const counterIncrement = () => {
  return {
    type: INCREMENT,
  };
};
export const counterDecrement = () => {
  return {
    type: DECREMENT,
  };
};
export const counterReset = () => {
  return {
    type: RESET,
  };
};
