import { CLOSE_DRAWER, OPEN_DRAWER, TOGGLE_DRAWER, TOGGLE_LOADER } from 'Store/constants/common.constant'

export const openDrawer = (status, id = null) => ({ type: OPEN_DRAWER, payload: { status, id } })
export const closeDrawer = (status, id = null) => ({ type: CLOSE_DRAWER, payload: { status, id } })

export const toggleLoader = payload => ({ type: TOGGLE_LOADER, payload: payload })
