import { color } from 'Assets/Style/themes'
import CustomButton from 'Components/Button/CustomButton'
import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import './spost.style.scss'

const SinglePost = props => {
  const [spost, setSpost] = useState({ ...post })
  const post = useSelector(state => state.posts.posts.find(post => post._id === props.match.params.id))

  useEffect(() => {
    setSpost({ ...post })
  }, [post])

  return (
    <section className='spost-section'>
      <div className='post-box'>
        <div className='post-info'>
          <span className='tags'>{spost.tags}</span>
          <h2 className='title'>{spost.title}</h2>
          <p className='sub-title'>{spost.subTitle}</p>
          <p className='description'>{spost.description}</p>
          <CustomButton
            color={color.primary}
            onClick={() => {
              props.history.push('/posts')
            }}>
            Go Back To Post
          </CustomButton>
        </div>
      </div>
    </section>
  )
}

export default SinglePost
