import styled from "styled-components";

export const MessageContainer = styled.div`
  background-color: red;
  font-size: 1.5rem;
  padding:.5em 1em;
  border-left: 2px solid red;
  color: #fff;
  border-radius: .1em;


  p {
    margin: 0;
  }
`;
