import { CLOSE_DRAWER, OPEN_DRAWER, SUCCESS_MESSAGE } from 'Store/constants/common.constant'

const initialState = {
  isDrawerOpen: false,
  isSuccessMessage: '',
  isErrorMessage: '',
  isWarningMessage: '',
}

export const CommonReducer = (state = initialState, action) => {
  if (action.type === OPEN_DRAWER) {
    return {
      ...state,
      isDrawerOpen: true,
    }
  }
  if (action.type === CLOSE_DRAWER) {
    return {
      isDrawerOpen: false,
    }
  }
  if (action.type === SUCCESS_MESSAGE) {
    return {
      isDrawerOpen: true,
    }
  }

  return state
}
