import axios from 'axios'
import { store } from 'Store/store'

const axiosInstance = axios.create({
  baseURL: `http://localhost:5000`,
})

axiosInstance.interceptors.request.use(config => {
  const state = store.getState()
  const token = state.auth.token
  config.params = config.params || {}
  config.params['x-access-token'] = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MGQ1N2ZmYjkwOTc0OTI1NzYxZDVhOTUiLCJpYXQiOjE2MjQ2MTU1NDEsImV4cCI6MTYyNDYxOTE0MX0.pByu8LM-k5dn1Z5nzcvnO_ByOy1iMvxgQ-WLVoNQQ4g'
  return config
})

export default axiosInstance
