import { createPost, deletePost, getPosts, getSinglePost, updatePost } from 'Services/posts.service'
import { post_type } from 'Store/constants/post.constant'
// import { CONFIRMED_CREATE_POST_ACTION, CONFIRMED_DELETE_POST_ACTION, CONFIRMED_EDIT_POST_ACTION, CONFIRMED_GET_POST, CONFIRMED_GET_POSTS, GET_POSTS_BY_TAG, GET_SINGLE_POST, SET_SINGLE_POST } from 'Store/constants/post.constant'
import { closeDrawer } from './common.action'

// get posts from api
export const getPostsAPI = () => async (dispatch, getState) => {
  try {
    const posts = await getPosts()
    const postsdata = posts.data
    dispatch({
      type: post_type.GET_POSTS,
      payload: postsdata,
    })
  } catch (error) {
    console.log('get post action error', error)
  }
}

// get single post from api
export const getSinglePostAPI = id => async dispatch => {
  try {
    const post = await getSinglePost(id)
    dispatch({
      type: post_type.GET_SINGLE_POST,
      payload: post,
    })
  } catch (error) {
    console.log('get post action error', error)
  }
}

// get filtered post from store
export const getPostActionByTag = tags => ({
  type: GET_POSTS_BY_TAG,
  payload: tags,
})
// get single post from store
export const confirmGetSinglePostAction = id => ({
  type: GET_SINGLE_POST,
  payload: id,
})
// create new post
export const createPostAction = postData => async (dispatch, getState) => {
  try {
    const result = await createPost(postData)
    const singlePost = result.data.lastpost
    dispatch(confirmedCreatePostAction(singlePost))
    dispatch(closeDrawer())
  } catch (error) {
    console.log('create post action error', error)
  }
}

export const confirmedCreatePostAction = post => ({
  type: CONFIRMED_CREATE_POST_ACTION,
  payload: post,
})

export const deletePostAction = postId => async (dispatch, getState) => {
  try {
    await deletePost(postId)
    dispatch(confirmedDeletePostAction(postId))
  } catch (error) {
    console.log('delete post action error', error)
  }
}

export const confirmedDeletePostAction = postId => ({
  type: CONFIRMED_DELETE_POST_ACTION,
  payload: postId,
})

export const updatePostAction = post => async (dispatch, getState) => {
  try {
    await updatePost(post._id, post)
    dispatch(confirmedUpdatePostAction(post))
    dispatch(closeDrawer())
  } catch (error) {
    console.log('update post action error', error)
  }
}

export const confirmedUpdatePostAction = post => ({
  type: CONFIRMED_EDIT_POST_ACTION,
  payload: post,
})
