import { CLOSE_DRAWER, OPEN_DRAWER, SHOW_MESSAGE } from 'Store/constants/common.constant'

export const openDrawer = () => ({ type: OPEN_DRAWER })
export const closeDrawer = () => ({ type: CLOSE_DRAWER })
export const successMessage = () => ({ type: SHOW_MESSAGE })
export const errorMessage = () => ({ type: SHOW_MESSAGE })
export const warningMessage = () => ({ type: SHOW_MESSAGE })
