import { Add } from '@material-ui/icons'
import CreatePostForm from 'Components/Post/CreatePostForm'
import EditPostForm from 'Components/Post/EditPostForm '
import Post from 'Components/Post/Post'
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link, Route, Switch } from 'react-router-dom'
import { toggleDrawer } from 'Store/actions/common.action'
import { getPostActionByTag, getPostsAction } from 'Store/actions/post.action'
import './Post.style.scss'

const Posts = props => {
  const [search, setSearch] = useState('')

  const dispatch = useDispatch()
  const posts = useSelector(state => state.posts.posts)

  const onSearch = e => {
    setSearch(e.target.value)
  }

  const onOpenCreatePost = () => {
    dispatch(toggleDrawer(true, 'CREATE_POST'))
  }
  useEffect(() => {
    if (search) {
      dispatch(getPostActionByTag(search))
    } else {
      dispatch(getPostsAction())
    }
  }, [search])

  return (
    <section className='post-section'>
      <div className='body'>
        <div className='filter-wrap'>
          <input type='text' className='filter-post' placeholder='Search by tags...' onChange={onSearch} value={search} />
          <span className='add-post-btn' onClick={onOpenCreatePost}>
            <Add /> Create New Post
          </span>
        </div>
        <div className='post-container'>
          {posts.map(post => (
            <Post key={post._id} {...post} history={props.history} />
          ))}
        </div>
      </div>
      {/* <Switch>
        <Route path='/posts/create-post' exact component={CreatePostForm} />
        {posts.length && <Route path='/posts/edit/:id' exact component={EditPostForm} />}
      </Switch> */}
      <CreatePostForm id='CREATE_POST' />
      {/* <EditPostForm id='EXIT_POST' /> */}
    </section>
  )
}

export default Posts
