import { color } from 'Assets/Style/themes'
import CustomButton from 'Components/Button/CustomButton'
import React, { useEffect, useState } from 'react'
import { connect, useDispatch, useSelector } from 'react-redux'
import { useParams } from 'react-router'
import { getSinglePostAction } from 'Store/actions/post.action'
import { getPost } from 'Store/selectors/Post.selectors'
import './Post.style.scss'

const SinglePost = props => {
  // const [post, setPost] = useState({ ...props.post })

  const post = useSelector(state => state.posts.post)
  const { tags, title, subTitle, description } = post
  const dispatch = useDispatch()
  const { id } = useParams()
  // useEffect(setPost() => {
  //   if (props.post) {
  //     (props.post)
  //   } else {
  //     props.history.push('/posts')
  //   }
  // }, [props.history, props.match.params, props.post])

  useEffect(() => {
    dispatch(getSinglePostAction(id))
  }, [id])

  return (
    <section className='spost-section'>
      <div className='post-box'>
        <div className='post-info'>
          <span className='tags'>{tags}</span>
          <h2 className='title'>{title}</h2>
          <p className='sub-title'>{subTitle}</p>
          <p className='description'>{description}</p>
          <CustomButton
            color={color.primary}
            onClick={() => {
              props.history.push('/posts')
            }}>
            Go Back To Post
          </CustomButton>
        </div>
      </div>
    </section>
  )
}

const mapStateToProps = state => {
  const post = getPost()
  return (state, props) => {
    return {
      post: post(state, props.match.params.id),
    }
  }
}

export default connect(mapStateToProps)(SinglePost)
