import { login, signup } from 'Services/auth.service'
import { AUTH_ERROR, LOGIN_SUCCESS, LOGOUT_SUCCESS } from 'Store/constants/auth.constant'
import { errorMessage, successMessage, toggleLoader } from './common.action'

export const signupAction = (userdata, history) => async dispatch => {
  try {
    const response = await signup(userdata)
    dispatch(successMessage(response.data.message))
    history.push('/login')
  } catch (e) {
    console.log('signup action error:', e)
    dispatch(errorMessage(e.message))
    dispatch(authError())
  }
  dispatch(toggleLoader(false))
}

export const loginAction = (userdata, history) => async dispatch => {
  try {
    const { user, token } = await login(userdata)

    dispatch(loginActionSuccess({ user, token }))
  } catch (e) {
    console.log('login action error:', e)
    dispatch(errorMessage(e.message))
    dispatch(authError())
  }
  dispatch(toggleLoader(false))
}

export const loginActionSuccess = userdata => ({
  type: LOGIN_SUCCESS,
  payload: userdata,
})

export const authError = () => ({
  type: AUTH_ERROR,
})

export const logoutAction = history => {
  history.push('/login')
  return {
    type: LOGOUT_SUCCESS,
  }
}
