import styled,{transparentize} from "styled-components";
import { color as colors } from "Assets/Style/themes";

export const MessageContainer = styled.div`
  background-color: ${transparentize(0.1, '#000')};
  font-size: 1.4rem;
  padding:.5em 1em;
  border-left: 2px solid ${({ color }) => color || colors.primary};
  color: #fff;
  /* font-weight: ${({ color }) => color || colors.primary}; */


  p {
    margin: 0;
  }
`;
